void Map::load(Texture* grayTexture, Texture* redTexture) {
    for (int i = 0; i <= MAP_HEIGHT; i++) {
        for (int j = 0; j < MAP_WIDTH; j++) {
            int x = 32 * j;
            int y = 32 * i;
            if (tilesMap[MAP_WIDTH * i + j] == 2) {
                Tile tile = Tile{grayTexture, GROUND, x, y};
                tiles.push_back(tile);
            } else if (tilesMap[MAP_WIDTH * i + j] == 11) {
                Tile tile = Tile{redTexture, WALL, x, y};
                tiles.push_back(tile);
            } else if (tilesMap[MAP_WIDTH * i + j] == 10) {
                Tile tile = Tile{grayTexture, ENEMY_PATH, x, y};
                tiles.push_back(tile);
            }
        }
    }
}

void Map::draw(Camera* camera) {
    for (auto &tile : tiles) {
        if (Collision::AABB(tile.getRect(), camera->getRect())) {
            tile.render(tile.getX() - camera->x, tile.getY() - camera->y);
        }
    }
}

Map::~Map() {
    if (!tilesMap.empty()) {
        //todo: destroy all textures??
        tilesMap = {};
    }
}

std::vector<Tile> Map::getTiles() const {
    return tiles;
}

Tile Map::getTileAbove(const Tile& tile) {
    long tileIndex = distance(tiles.begin(), find(tiles.begin(), tiles.end(), tile));

    if (tileIndex - MAP_WIDTH < 0) {
        return tile;
    }

    return tiles.at(tileIndex - MAP_WIDTH);
}

Tile Map::getTileBelow(const Tile& tile) {
    long tileIndex = distance(tiles.begin(), find(tiles.begin(), tiles.end(), tile));
    if (tileIndex + MAP_WIDTH > tiles.size() + 1) {
        return tile;
    }

    return tiles.at(tileIndex + MAP_WIDTH);
}

Tile Map::getTileToRight(const Tile& tile) {
    long tileIndex = distance(tiles.begin(), find(tiles.begin(), tiles.end(), tile));
    if (tileIndex + 1 > tiles.size() + 1) {
        return tile;
    }

    return tiles.at(tileIndex + 1);
}

Tile Map::getTileToLeft(const Tile& tile) {
    long tileIndex = distance(tiles.begin(), find(tiles.begin(), tiles.end(), tile));
    if (tileIndex - 1 < 0) {
        return tile;
    }

    return tiles.at(tileIndex - 1);
}
